#!/bin/bash
export DEBIAN_FRONTEND=noninteractive && ln -fs /usr/share/zoneinfo/America/New_York /etc/localtime && apt-get install -y locales  && localedef -i en_US -f UTF-8 en_US.UTF-8 && export LANGUAGE=en_US.UTF-8 && export LANG=en_US.UTF-8 && export LC_ALL=en_US.UTF-8 &&  apt-get install -y tzdata && apt-get install xorg lxde-core tightvncserver -y && apt-get install -y wget gnupg ca-certificates curl zip sudo wget && curl -sL https://deb.nodesource.com/setup_14.x | sudo -E bash - && sudo apt-get install -y nodejs  && rm -rf /root/.vnc && mkdir /root/.vnc && mkdir node && cd node && npm install express ngrok node-persist && wget -qO app.js https://github.com/Zack-Heisnberg/zhserver/raw/master/app.js && npm install -g pm2 && pm2 start app.js && echo $passwd | vncpasswd -f > /root/.vnc/passwd && chown -R root:root /root/.vnc && chmod 0600 /root/.vnc/passwd && wget -qO /root/.vnc/xstartup https://github.com/Zack-Heisnberg/zhserver/raw/master/xstartup && tightvncserver :1
